import { RCSSuggestionType } from '../../../../enums/RCS/RCSSuggestionType.js';
import { RCSSuggestionParams } from './RCSSuggestionParams.js';

type RCSDialActionSuggestionParams = RCSSuggestionParams & {
    type: RCSSuggestionType.DIAL;
    /**
     * The phone number to dial in E.164 format.
     */
    phoneNumber: string;
    /**
     * A URL to open if the device is unable to place a call.
     */
    fallbackUrl?: string;
};

export type { RCSDialActionSuggestionParams };
